'use client'

import { useEffect, useState } from 'react'
import { Card, CardContent } from '@/components/ui/card'
import { Label } from '@/components/ui/label'
import { Select, SelectTrigger, SelectValue, SelectContent, SelectItem } from '@/components/ui/select'
import { motion } from 'framer-motion'
import { format, parseISO } from 'date-fns'
import { ptBR } from 'date-fns/locale'

interface Influenciador {
  id: string
  nome: string
  cadastros_influenciadores: {
    recargas: {
      meta: number
      atingido: number
    }[]
  }[]
  salarios_mensais: {
    valor: number
    mes: number
    ano: number
  }[]
  salario_fixo: number | null
}

interface Recarga {
  inicio: string
  atingido: number
  salario: number
}

export default function DashboardPage() {
  const [influenciadores, setInfluenciadores] = useState<Influenciador[]>([])
  const [recargas, setRecargas] = useState<Recarga[]>([])
  const [mesSelecionado, setMesSelecionado] = useState(new Date().getMonth() + 1)

  useEffect(() => {
    fetch('/api/influenciadores')
      .then(res => res.json())
      .then(data => setInfluenciadores(data))

    fetch('/api/recargas')
      .then(res => res.json())
      .then(data => setRecargas(data))
  }, [])

  const receitaMensal = recargas
    .filter(r => new Date(r.inicio).getMonth() + 1 === mesSelecionado)
    .reduce((acc, r) => acc + r.atingido, 0)

  const gastoComRecargas = recargas
    .filter(r => new Date(r.inicio).getMonth() + 1 === mesSelecionado)
    .reduce((acc, r) => acc + r.salario, 0)

  const gastoComSalarios = influenciadores.reduce((acc, inf) => {
    const fixo = inf.salario_fixo || 0
    const mensal = inf.salarios_mensais
      .filter(s => s.mes === mesSelecionado)
      .reduce((soma, s) => soma + s.valor, 0)
    return acc + fixo + mensal
  }, 0)

  const despesaTotal = gastoComRecargas + gastoComSalarios

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center gap-4">
        <Label>M√™s:</Label>
        <Select onValueChange={(value) => setMesSelecionado(Number(value))} defaultValue={String(mesSelecionado)}>
          <SelectTrigger className="w-40">
            <SelectValue placeholder="Selecione o m√™s" />
          </SelectTrigger>
          <SelectContent>
            {[...Array(12)].map((_, i) => (
              <SelectItem key={i + 1} value={String(i + 1)}>
                {format(new Date(2023, i), 'MMMM', { locale: ptBR })}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        <CardMetrica titulo="üí∞ Receita da ag√™ncia" valor={receitaMensal} cor="text-green-500" />
        <CardMetrica titulo="üí∏ Gasto com recargas" valor={gastoComRecargas} cor="text-yellow-500" />
        <CardMetrica titulo="üßæ Gasto com sal√°rios" valor={gastoComSalarios} cor="text-red-500" />
        <CardMetrica titulo="üìâ Despesa total" valor={despesaTotal} cor="text-red-700" />
      </div>
    </div>
  )
}

function CardMetrica({ titulo, valor, cor }: { titulo: string, valor: number, cor: string }) {
  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.4 }}
    >
      <Card className="shadow-lg">
        <CardContent className="p-4">
          <div className="text-sm text-muted-foreground">{titulo}</div>
          <div className={`text-2xl font-bold ${cor}`}>R$ {valor.toLocaleString('pt-BR', { minimumFractionDigits: 2 })}</div>
        </CardContent>
      </Card>
    </motion.div>
  )
}
