'use client'

import { useState, useEffect } from 'react'
import { Input } from '@/components/ui/input'
import { Button } from '@/components/ui/button'
import { PlusCircle, Loader2, Trash2 } from 'lucide-react'
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
  DialogFooter,
  DialogTrigger,
} from '@/components/ui/dialog'
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select'
import { Label } from '@/components/ui/label'
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog"


// --- Interfaces (Mantidas) ---
interface Participante {
  id: string
  influencer_id: string
  nome: string
  imagem: string
  atingido: number
  meta: number
}

interface InfluencerOption {
  id: string
  nome: string | null
  imagem: string | null
}

interface Props {
  participantes: Participante[]
  eventoId: string
}

export default function ListaParticipantesEvento({ participantes: initialParticipantes, eventoId }: Props) {
  const [participantes, setParticipantes] = useState<Participante[]>(initialParticipantes)
  const [modalMode, setModalMode] = useState<'add' | 'edit'>('add')
  const [editingParticipant, setEditingParticipant] = useState<Participante | null>(null)

  // --- Estados do Modal Adicionar/Editar ---
  const [isModalOpen, setIsModalOpen] = useState(false)
  const [newInfluencerForm, setNewInfluencerForm] = useState({
    influencer_id: '',
    meta: 0,
    atingido: 0,
  })
  const [isSaving, setIsSaving] = useState(false); // <-- NOVO ESTADO: Para controlar o salvamento

  // --- Estados para a lista de Influenciadores Disponﾃｭveis ---
  const [availableInfluencers, setAvailableInfluencers] = useState<InfluencerOption[]>([])
  const [loadingInfluencers, setLoadingInfluencers] = useState(true)
  const [errorLoadingInfluencers, setErrorLoadingInfluencers] = useState<string | null>(null)

  // --- Estados para o Modal de Confirmaﾃｧﾃ｣o de Exclusﾃ｣o ---
  const [isConfirmDeleteDialogOpen, setIsConfirmDeleteDialogOpen] = useState(false);
  const [participantToDelete, setParticipantToDelete] = useState<Participante | null>(null);


  // --- Efeito para buscar a lista de influenciadores ---
  useEffect(() => {
    const fetchInfluencers = async () => {
      try {
        setLoadingInfluencers(true)
        const res = await fetch('/api/influenciadores')
        if (!res.ok) throw new Error(`Erro: ${res.statusText}`)
        const data = await res.json() as any[]

        const options: InfluencerOption[] = data.map((inf: any) => ({
          id: inf.id,
          nome: inf.nome || null,
          imagem: inf.imagem || null,
        }))
        setAvailableInfluencers(options)
      } catch (err: any) {
        console.error("Erro ao buscar influenciadores:", err)
        setErrorLoadingInfluencers(err.message)
      } finally {
        setLoadingInfluencers(false)
      }
    }
    fetchInfluencers()
  }, [])

  // --- Lﾃｳgica para abrir modal de adiﾃｧﾃ｣o ---
  const handleAddClick = () => {
    setModalMode('add');
    setEditingParticipant(null);
    setNewInfluencerForm({ influencer_id: '', meta: 0, atingido: 0 });
    setIsModalOpen(true);
  };

  // --- Lﾃｳgica para abrir modal de ediﾃｧﾃ｣o ---
  const handleEditar = (p: Participante) => {
    setModalMode('edit')
    setEditingParticipant(p)
    setNewInfluencerForm({
      influencer_id: p.influencer_id, // Manter o ID para referﾃｪncia, embora desabilitado no Select
      meta: p.meta,
      atingido: p.atingido,
    })
    setIsModalOpen(true)
  }

   // --- Lﾃｳgica para fechar modal de adiﾃｧﾃ｣o/ediﾃｧﾃ｣o e resetar estado ---
   const handleCloseModal = (open: boolean) => {
      setIsModalOpen(open);
      if (!open) { // Se estiver fechando o modal
         setEditingParticipant(null);
         setNewInfluencerForm({ influencer_id: '', meta: 0, atingido: 0 });
         setModalMode('add');
      }
   };

  // --- Lﾃｳgica para fechar modal de confirmaﾃｧﾃ｣o de exclusﾃ｣o ---
  const handleCloseConfirmDeleteDialog = (open: boolean) => {
      setIsConfirmDeleteDialogOpen(open);
      if (!open) { // Se estiver fechando o modal de confirmaﾃｧﾃ｣o
          setParticipantToDelete(null); // Limpa o participante a ser excluﾃｭdo
      }
  };

  // --- Lﾃｳgica para selecionar influenciador no formulﾃ｡rio de adiﾃｧﾃ｣o ---
  const handleSelectInfluencer = (value: string) => {
    setNewInfluencerForm(prev => ({ ...prev, influencer_id: value }))
  }

  // --- Lﾃｳgica para atualizar campos Meta/Atingido no formulﾃ｡rio ---
  const handleNewFormChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type } = e.target
    setNewInfluencerForm(prev => ({
      ...prev,
      [name]: type === 'number' ? +value : value,
    }))
  }

  // --- Lﾃｳgica para Salvar (Adicionar ou Editar) ---
  const handleSave = async () => {
    const { influencer_id, meta, atingido } = newInfluencerForm

    // --- Validaﾃｧﾃ｣o e Verificaﾃｧﾃ｣o de Duplicidade (Modo Adicionar) ---
    if (modalMode === 'add') {
       if (!influencer_id) {
           alert('Selecione um influenciador.');
           return;
       }
       // <-- VERIFICAﾃﾃグ DE DUPLICIDADE AQUI -->
       const isDuplicate = participantes.some(p => p.influencer_id === influencer_id);
       if (isDuplicate) {
           alert('Este influenciador jﾃ｡ participa deste evento.');
           return; // Impede a continuaﾃｧﾃ｣o se for duplicado
       }
       // <------------------------------------>
    }

     if (isNaN(meta) || meta < 0 || isNaN(atingido) || atingido < 0) {
       alert('Meta e Atingido devem ser nﾃｺmeros vﾃ｡lidos e nﾃ｣o negativos.');
       return;
     }
    // --- Fim da Validaﾃｧﾃ｣o ---

    setIsSaving(true); // <-- NOVO: Habilita estado de salvamento

    try {
      const method = modalMode === 'add' ? 'POST' : 'PATCH'
      const url = `/api/eventos/${eventoId}/participantes`; // URL base para POST e PATCH

      const body = modalMode === 'add'
        ? newInfluencerForm // POST body
        : {
            participante_id: editingParticipant?.id, // PATCH body
            meta,
            atingido,
          }

      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body),
      })

      if (!res.ok) {
        const errorBody = await res.text();
        throw new Error(`Erro ao salvar participante: ${res.status} - ${errorBody || res.statusText}`);
      }

      const response = await res.json();

      if (modalMode === 'add') {
        const selected = availableInfluencers.find(i => i.id === influencer_id);
        const novoParticipante: Participante = {
            id: response.id,
            influencer_id: influencer_id,
            nome: selected?.nome || `ID: ${influencer_id}`,
            imagem: selected?.imagem || '',
            meta: response.meta,
            atingido: response.atingido,
        };
        setParticipantes(prev => [...prev, novoParticipante]);

      } else { // Modo 'edit'
        setParticipantes(prev => prev.map(p =>
          p.id === editingParticipant?.id ? { ...p, meta: response.meta, atingido: response.atingido } : p
        ));
      }

      handleCloseModal(false); // Fechar e resetar modal de adiﾃｧﾃ｣o/ediﾃｧﾃ｣o
    } catch (err: any) {
      console.error("Erro no handleSave:", err); // Log mais detalhado
      alert(err.message)
    } finally {
        setIsSaving(false); // <-- NOVO: Desabilita estado de salvamento (acontece sempre)
    }
  }

    // --- Lﾃｳgica para iniciar o processo de exclusﾃ｣o (mostrar confirmaﾃｧﾃ｣o) ---
    const handleStartDelete = (p: Participante) => {
        setParticipantToDelete(p);
        setIsConfirmDeleteDialogOpen(true);
    };

    // --- Lﾃｳgica para Excluir (apﾃｳs confirmaﾃｧﾃ｣o) ---
    const handleDeleteParticipant = async () => {
        if (!participantToDelete) return;

        try {
            const url = `/api/eventos/${eventoId}/participantes/${participantToDelete.id}`;

            const res = await fetch(url, {
                method: 'DELETE',
                headers: { 'Content-Type': 'application/json' },
            });

            if (!res.ok) {
                const errorBody = await res.text();
                throw new Error(`Erro ao excluir participante: ${res.status} - ${errorBody || res.statusText}`);
            }

            setParticipantes(prev => prev.filter(p => p.id !== participantToDelete.id));

            handleCloseConfirmDeleteDialog(false);

        } catch (err: any) {
            console.error("Erro ao excluir participante:", err);
            alert(`Erro ao excluir participante: ${err.message}`);
        }
    };

    // --- Calcula se o influenciador selecionado jﾃ｡ estﾃ｡ na lista (para desabilitar botﾃ｣o) ---
    const isInfluencerAlreadyAdded = modalMode === 'add' &&
                                     newInfluencerForm.influencer_id !== '' &&
                                     participantes.some(p => p.influencer_id === newInfluencerForm.influencer_id);
    // <------------------------------------------------------------------------>


  // --- Renderizaﾃｧﾃ｣o ---
  return (
    <div className="bg-white rounded-lg shadow p-4 w-full h-full overflow-auto">
      <div className='flex'>
      <h2 className="text-xl font-bold mb-4">Lista de Influenciadores</h2>
      {/* Botﾃ｣o para Adicionar Influenciador - Trigger do Modal Adicionar/Editar */}
      <Dialog open={isModalOpen} onOpenChange={handleCloseModal}>
        <DialogTrigger asChild>
          <Button size="sm" className="mb-4" onClick={handleAddClick}>
            <PlusCircle className="mr-2 h-4 w-4" /> Adicionar Influenciador
          </Button>
        </DialogTrigger>

        {/* Conteﾃｺdo do Modal Adicionar/Editar */}
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle>
              {modalMode === 'add' ? 'Adicionar Novo Participante' : 'Editar Participante'}
            </DialogTitle>
            <DialogDescription>
              {modalMode === 'add'
                ? 'Selecione um influenciador e defina as mﾃｩtricas iniciais para este evento.'
                : `Editando mﾃｩtricas para: ${editingParticipant?.nome ?? 'Participante'}.`}
            </DialogDescription>
          </DialogHeader>

          <div className="grid gap-4 py-4">
            {/* Campo Influenciador (Select) */}
            <div className="space-y-1.5">
              <Label className="text-right">Influenciador</Label>
              <div className="col-span-3"> {/* Ajuste de layout, remova col-span-3 se nﾃ｣o precisar */}
                {loadingInfluencers ? (
                  <div className="flex items-center gap-2 text-gray-500">
                    <Loader2 className="h-4 w-4 animate-spin" /> Carregando...
                  </div>
                ) : errorLoadingInfluencers ? (
                  <p className="text-red-500 text-sm">{errorLoadingInfluencers}</p>
                ) : (
                  <Select
                    onValueChange={handleSelectInfluencer}
                    value={newInfluencerForm.influencer_id}
                    disabled={modalMode === 'edit'} // Desabilita seleﾃｧﾃ｣o em modo ediﾃｧﾃ｣o
                  >
                    <SelectTrigger>
                      {newInfluencerForm.influencer_id
                        ? availableInfluencers.find(i => i.id === newInfluencerForm.influencer_id)?.nome || `ID: ${newInfluencerForm.influencer_id}`
                        : 'Selecione um influenciador'}
                    </SelectTrigger>
                    <SelectContent>
                      {availableInfluencers.map((inf) => (
                        <SelectItem key={inf.id} value={inf.id}>
                           {inf.nome || `Sem nome - ID: ${inf.id}`}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                )}
                {/* Mensagem de duplicidade opcional */}
                {modalMode === 'add' && isInfluencerAlreadyAdded && (
                    <p className="text-sm font-medium text-yellow-600 mt-1">
                        Este influenciador jﾃ｡ estﾃ｡ neste evento.
                    </p>
                )}
              </div>
            </div>

            {/* Campos Meta e Atingido */}
            <div className='grid grid-cols-2 items-center gap-4'>
                <div className="space-y-1.5">
                  <Label className="text-right">Meta</Label>
                  <Input
                    name="meta"
                    type="number"
                    value={newInfluencerForm.meta}
                    onChange={handleNewFormChange}
                    className="flex-grow"
                  />
                </div>

                <div className="space-y-1.5">
                  <Label className="text-right">Atingido</Label>
                  <Input
                    name="atingido"
                    type="number"
                    value={newInfluencerForm.atingido}
                    onChange={handleNewFormChange}
                    className="flex-grow"
                  />
                </div>
            </div>
          </div>

          <DialogFooter>
            <Button variant="outline" onClick={() => handleCloseModal(false)}>Cancelar</Button>
            <Button
              type="button"
              onClick={handleSave}
              // <-- DESABILITAﾃﾃグ DO BOTﾃグ AQUI -->
              disabled={
                isSaving || // Desabilita se estiver salvando
                loadingInfluencers || // Opcional: desabilita se ainda carregando a lista de influenciadores
                (modalMode === 'add' && !newInfluencerForm.influencer_id) || // Em modo adicionar, desabilita se nenhum influencer selecionado
                isInfluencerAlreadyAdded // <-- NOVO: Desabilita se o influenciador selecionado jﾃ｡ estﾃ｡ na lista
              }
            >
              {/* ﾃ皇one de loading condicional */}
              {isSaving && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
              {modalMode === 'add' ? 'Salvar Influenciador' : 'Atualizar'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
      </div>


      {/* Modal de Confirmaﾃｧﾃ｣o de Exclusﾃ｣o (Mantido) */}
      <AlertDialog open={isConfirmDeleteDialogOpen} onOpenChange={handleCloseConfirmDeleteDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Tem certeza?</AlertDialogTitle>
            <AlertDialogDescription>
              Esta aﾃｧﾃ｣o nﾃ｣o pode ser desfeita. Isso removerﾃ｡ permanentemente o participante{' '}
              <strong>{participantToDelete?.nome ?? `ID: ${participantToDelete?.influencer_id ?? ''}`}</strong>{' '}
              deste evento.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel onClick={() => handleCloseConfirmDeleteDialog(false)}>Cancelar</AlertDialogCancel>
            <AlertDialogAction onClick={handleDeleteParticipant}>Continuar</AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>


      {/* Tabela de Participantes (Mantida) */}
      <table className="w-full text-left border-separate border-spacing-y-2">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Atingido</th>
            <th>Meta</th>
            <th>Aﾃｧﾃｵes</th>
          </tr>
        </thead>
        <tbody>
          {participantes.map(p => (
            <tr key={p.id} className="bg-gray-100 rounded">
              <td>{p.nome ?? `ID: ${p.influencer_id}`}</td>
              <td>{p.atingido}</td>
              <td>{p.meta}</td>
              <td className="flex gap-2">
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => handleEditar(p)}
                >
                  Editar
                </Button>
                <Button
                  size="sm"
                  variant="shadow"
                  onClick={() => handleStartDelete(p)}
                >
                   <Trash2 className="h-4 w-4" />
                 </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}
