'use client';

import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { KanbanInfluenciador } from "./data";

type Props = {
  task: KanbanInfluenciador;
};

export default function TaskCard({ task }: Props) {
  return (
    <Card className="shadow-md">
      <CardHeader className="flex-row items-center gap-3 pb-2">
        <Avatar className="h-10 w-10">
          <AvatarImage src={task.imagem} />
          <AvatarFallback>
            {task.nome.charAt(0).toUpperCase()}
          </AvatarFallback>
        </Avatar>
        <div className="flex-1">
          <p className="font-semibold text-sm truncate">{task.nome}</p>
          <p className="text-xs text-muted-foreground truncate">
            {task.instagram}
          </p>
        </div>
      </CardHeader>
      <CardContent className="text-xs text-muted-foreground space-y-1">
        <p>CPF: {task.cpf}</p>
        <p>Meta: R$ {task.meta.toLocaleString()}</p>
        <p>Atingido: R$ {task.atingido.toLocaleString()}</p>
        <p className="text-green-600 font-semibold">
          {task.porcentagem}% da meta
        </p>
      </CardContent>
    </Card>
  );
}
