'use client';

import { ColumnDef } from '@tanstack/react-table';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import { Button } from '@/components/ui/button';
import { Eye, SquarePen, Trash2 } from 'lucide-react';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { cn } from '@/lib/utils';

export type InfluenciadorLista = {
  id: string;
  nome: string;
  imagem?: string;
  instagram: string;
  cpf: string;
  meta: number;
  atingido: number;
  statusMeta: 'completo' | 'incompleto';
};

export const columns: ColumnDef<InfluenciadorLista>[] = [
  {
    id: 'select',
    header: ({ table }) => (
      <Checkbox
        checked={
          table.getIsAllPageRowsSelected() ||
          (table.getIsSomePageRowsSelected() && 'indeterminate')
        }
        onCheckedChange={(value) => table.toggleAllPageRowsSelected(!!value)}
        aria-label="Selecionar tudo"
      />
    ),
    cell: ({ row }) => (
      <Checkbox
        checked={row.getIsSelected()}
        onCheckedChange={(value) => row.toggleSelected(!!value)}
        aria-label="Selecionar linha"
      />
    ),
    enableSorting: false,
    enableHiding: false,
  },
  {
    accessorKey: 'nome',
    header: 'Nome',
    cell: ({ row }) => {
      const { nome, imagem } = row.original;
      return (
        <div className="flex items-center gap-3">
          <Avatar className="w-8 h-8">
            {imagem ? <AvatarImage src={imagem} /> : <AvatarFallback>INF</AvatarFallback>}
          </Avatar>
          <span>{nome}</span>
        </div>
      );
    },
  },
  {
    accessorKey: 'instagram',
    header: 'Instagram',
    cell: ({ row }) => (
      <a
        href={row.original.instagram}
        target="_blank"
        rel="noopener noreferrer"
        className="text-blue-600 underline text-sm"
      >
        @{row.original.instagram.replace('https://instagram.com/', '')}
      </a>
    ),
  },
  {
    accessorKey: 'cpf',
    header: 'CPF',
    cell: ({ row }) => <span>{row.original.cpf}</span>,
  },
  {
    accessorKey: 'meta',
    header: 'Meta',
    cell: ({ row }) => <span>R$ {row.original.meta.toLocaleString('pt-BR')}</span>,
  },
  {
    accessorKey: 'atingido',
    header: 'Atingido',
    cell: ({ row }) => <span>R$ {row.original.atingido.toLocaleString('pt-BR')}</span>,
  },
  {
    accessorKey: 'statusMeta',
    header: 'Status',
    cell: ({ row }) => {
      const status = row.original.statusMeta;
      const color = status === 'completo' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800';
      return <Badge className={cn('rounded-full px-3 py-1 text-xs', color)}>{status}</Badge>;
    },
  },
  {
    id: 'actions',
    header: 'A√ß√µes',
    cell: () => (
      <div className="flex gap-2">
        <TooltipProvider>
          <Tooltip>
            <TooltipTrigger asChild>
              <Button size="icon" variant="outline">
                <Eye className="w-4 h-4" />
              </Button>
            </TooltipTrigger>
            <TooltipContent>Visualizar</TooltipContent>
          </Tooltip>
        </TooltipProvider>
        <TooltipProvider>
          <Tooltip>
            <TooltipTrigger asChild>
              <Button size="icon" variant="outline">
                <SquarePen className="w-4 h-4" />
              </Button>
            </TooltipTrigger>
            <TooltipContent>Editar</TooltipContent>
          </Tooltip>
        </TooltipProvider>
        <TooltipProvider>
          <Tooltip>
            <TooltipTrigger asChild>
              <Button size="icon" variant="outline">
                <Trash2 className="w-4 h-4" />
              </Button>
            </TooltipTrigger>
            <TooltipContent>Excluir</TooltipContent>
          </Tooltip>
        </TooltipProvider>
      </div>
