'use client';

import { useParams } from 'next/navigation';
import React, { useEffect, useState } from 'react';
import ListaInfluenciadores from './components/lista/table';
import KanbanWrapper from './components/kanban';
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from "@/components/ui/tabs";
import { Card } from '@/components/ui/card';
import Image from 'next/image'; // Para carregar imagem certinho

interface Plataforma {
  id: string;
  nome: string;
  imagem?: string;
  cor: string;
}

export default function PaginaPlataforma() {
  const params = useParams();
  const plataformaSlug = params?.id as string;

  const [plataformaAtual, setPlataformaAtual] = useState<Plataforma | null>(null);

  useEffect(() => {
    const fetchPlataforma = async () => {
      try {
        const res = await fetch('/api/plataformas');
        const plataformas: Plataforma[] = await res.json();
        const plataforma = plataformas.find(p => p.id === plataformaSlug);
        setPlataformaAtual(plataforma || null);
      } catch (error) {
        console.error('Erro ao buscar plataformas:', error);
      }
    };

    fetchPlataforma();
  }, [plataformaSlug]);

  return (
    <div className="p-4 space-y-4">
      
      {/* Imagem Centralizada */}
      {plataformaAtual && (
        <div className="flex justify-center mb-4">
          <Image
            src={plataformaAtual.imagem || '/placeholder.png'}
            alt={plataformaAtual.nome}
            width={200}
            height={80}
            className="object-contain"
          />
        </div>
      )}

      {/* Tabs */}
      <Tabs defaultValue="kanban" className="w-full">
        <TabsList className="flex-wrap">
          <TabsTrigger
            value="kanban"
            className="data-[state=active]:bg-primary data-[state=active]:text-primary-foreground rounded-md px-6"
          >
            Kanban
          </TabsTrigger>
          <TabsTrigger
            value="lista"
            className="data-[state=active]:bg-primary data-[state=active]:text-primary-foreground rounded-md px-6"
          >
            Lista
          </TabsTrigger>
        </TabsList>

        <TabsContent value="kanban">
          <Card>
            <KanbanWrapper />
          </Card>
        </TabsContent>
        <TabsContent value="lista">
          <Card>
            <ListaInfluenciadores plataformaSlug={plataformaSlug} />
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}
