'use client';

import { useParams } from 'next/navigation';
import { useEffect } from 'react';

export default function TesteGrupo() {
  const { grupoId } = useParams() as { grupoId: string }; // <- ajustado aqui

  useEffect(() => {
    console.log('[CLIENT] P치gina de grupo aberta com ID:', grupoId);
  }, [grupoId]);

  return (
    <div className="p-6 space-y-2">
      <h1 className="text-2xl font-bold text-red-500">P치gina do grupo: {grupoId}</h1>
      <p className="text-sm text-gray-600">Teste simples sem nenhum layout extra.</p>
    </div>
  );
}
