'use client';

import { ReactNode } from 'react';
import { ThemeProvider } from '@/providers/theme-provider';
import MountedProvider from '@/providers/mounted.provider';
import { Toaster } from '@/components/ui/toaster';
import { Toaster as SonnerToaster } from '@/components/ui/sonner';

// Se precisar de outros providers para influenciadores, importe aqui

interface InfluenciadoresLayoutProps {
  children: ReactNode;
}

export default function InfluenciadoresLayout({ children }: InfluenciadoresLayoutProps) {
  return (
    <ThemeProvider attribute="class" defaultTheme="light">
      <MountedProvider>
        <div className="min-h-screen bg-background text-foreground">
          {/* Aqui você pode adicionar um header ou sidebar específicos de influenciadores, se quiser */}
          {children}
        </div>
        <Toaster />
        <SonnerToaster />
      </MountedProvider>
    </ThemeProvider>
  );
}
