import { ColumnDef } from "@tanstack/react-table";
import { Select as ShadSelect, SelectTrigger, SelectValue, SelectContent, SelectItem } from "@/components/ui/select";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";
import { Eye } from "lucide-react";

export type LinhaReembolso = {
  recargaId: string;
  nome: string;
  imagem?: string;
  instagram: string;
  cpf: string;
  plataforma: string;
  inicio: string;
  termino: string;
  meta: number;
  atingido: number;
  reembolso: number;
  reembolsoStatus: "pendente" | "pago";
};

type Props = {
  onUpdateStatus: (recargaId: string, newStatus: "pendente" | "pago") => void;
  onView: (recarga: LinhaReembolso) => void;
};

export const getColumns = ({ onUpdateStatus, onView }: Props): ColumnDef<LinhaReembolso>[] => [
  {
    accessorKey: "nome",
    header: "Influenciador",
    cell: ({ row }) => {
      const { nome, imagem } = row.original;
      return (
        <div className="flex items-center gap-2">
          <Avatar className="w-8 h-8">
            {imagem ? <AvatarImage src={imagem} /> : <AvatarFallback>INF</AvatarFallback>}
          </Avatar>
          <span>{nome}</span>
        </div>
      );
    },
  },
  {
    accessorKey: "instagram",
    header: "Instagram",
    cell: ({ row }) => (
      <a
        href={`https://instagram.com/${row.original.instagram}`}
        target="_blank"
        rel="noopener noreferrer"
        className="text-blue-600 underline text-sm"
      >
        @{row.original.instagram}
      </a>
    ),
  },
  {
    accessorKey: "cpf",
    header: "CPF",
  },
  {
    accessorKey: "plataforma",
    header: "Plataforma",
  },
  {
    accessorKey: "meta",
    header: "Meta (R$)",
    cell: ({ row }) => (
      <span>R$ {row.original.meta.toLocaleString('pt-BR')}</span>
    ),
  },
  {
    accessorKey: "atingido",
    header: "Atingido (R$)",
    cell: ({ row }) => (
      <span>R$ {row.original.atingido.toLocaleString('pt-BR')}</span>
    ),
  },
  {
    accessorKey: "reembolso",
    header: "Reembolso (R$)",
    cell: ({ row }) => (
      <span className={row.original.reembolso > 0 ? "text-red-500" : "text-green-600"}>
        R$ {row.original.reembolso.toLocaleString('pt-BR')}
      </span>
    ),
  },
  {
    accessorKey: "reembolsoStatus",
    header: "Status do Reembolso",
    cell: ({ row }) => {
      const status = row.original.reembolsoStatus;
  
      return (
        <ShadSelect value={status} onValueChange={(value) => onUpdateStatus(row.original.recargaId, value as any)}>
          <SelectTrigger className="w-[120px]">
            <SelectValue placeholder="Status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="pendente">Pendente</SelectItem>
            <SelectItem value="pago">Pago</SelectItem>
          </SelectContent>
        </ShadSelect>
      );
    },
  },
  {
    id: 'actions',
    header: 'A√ß√µes',
    cell: ({ row }) => (
      <div className="flex gap-2">
        <TooltipProvider>
          <Tooltip>
            <TooltipTrigger asChild>
              <Button size="icon" variant="outline" onClick={() => onView(row.original)}>
                <Eye className="w-4 h-4" />
              </Button>
            </TooltipTrigger>
            <TooltipContent>Visualizar</TooltipContent>
          </Tooltip>
        </TooltipProvider>
      </div>
    ),
  },
];
