'use client';
import Image from 'next/image';
import { useTheme } from "next-themes";

type LogoProps = {
  className?: string;
};

const Logo = ({ className = "" }: LogoProps) => {
  const { theme: mode } = useTheme();

  return (
    <div className={className}>
      <Image
        key={mode}
        src={
          mode === "light"
            ? "/images/logo/logo.svg"
            : "/images/logo/logo-white.svg"
        }
        alt="Logo"
        width={300}
        height={300}
        className="w-36"
        priority
      />
    </div>
  );
};

export default Logo;
